import React, { useState } from 'react';
import './Chatbot.scss';
import OpenAI from "openai";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');


  //Chatbot intergration
  const openai = new OpenAI({
    apiKey: "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",
  });
  
  const completion = openai.chat.completions.create({
    model: "gpt-4o-mini",
    store: true,
    messages: [
      {"role": "user", "content": "write a haiku about ai"},
    ],
  });
  
  const handleSend = () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input };
    setMessages([...messages, userMessage]);

    setTimeout(() => {
      const botResponse = { sender: 'bot', text: 'Hello! How can I help you?' };
      setMessages((prev) => [...prev, botResponse]);
    }, 1000);

    setInput('');
  };
  

  return (
    <div className="chatbot">
      <div className="chat-icon">ðŸ’¬</div>
      <div className="chat-window">
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chatbot;
